import { useState } from "react";
import './App.css';

//for toastyfy
import { ToastContainer, toast } from 'react-toastify';
//for toastyfy
import 'react-toastify/dist/ReactToastify.css';
import { numbers, upperCaseLetters, lowerCaseLetters, specialCharacters } from "./components/Character";
import { COPY_Fail,COPY_SUCCESS } from "./components/Message";


function App() {

  return (
    <div className="App">
      <div className="container">
        <div className="generator">
          <h2 className="generator_header">
            Password Generator
          </h2>
         <div className="generate_password">
         <button className="copy_button">
            <i className="far fa-clipboard"></i>
          </button>
         </div>
         {/* Forms */}
         <div className="form-group">
            <label htmlFor="password-strength">Password length</label>
            <input className="pw"  type="number" id="password-stregth" name="password-strength" max="26" min="8" />
          </div>
          <div className="form-group">
            <label htmlFor="uppercase-letters">Add Uppercase Letters</label>
            <input type="checkbox" id="uppercase-letters" name="uppercase-letters" />
          </div>
          <div className="form-group">
            <label htmlFor="lowercase-letters">Add Lowercase Letters</label>
            <input type="checkbox" id="lowercase-letters" name="lowercase-letters" />
          </div>
          <div className="form-group">
            <label htmlFor="include-numbers">Include Numbers</label>
            <input  type="checkbox" id="include-numbers" name="include-numbers" />
          </div>
          <div className="form-group">
            <label htmlFor="include-symbols">Include Symbols</label>
            <input  type="checkbox" id="include-symbols" name="include-symbols" />
          </div>
         <button className="generator__btn">
            Generate Password
          </button>
          <ToastContainer
            position="top-center"
            autoClose={5000}
            hideProgressBar={false}
            newestOnTop={false}
            closeOnClick
            rtl={false}
            pauseOnFocusLoss
            draggable
            pauseOnHover
          />
        </div>
      </div>

    </div>
  );
}

export default App;
