import { Box, Button, Checkbox, Flex, Spacer, Stack} from '@chakra-ui/react'
import React from 'react'
import { useSelector,useDispatch } from 'react-redux'
import { toggle } from '../redux/todos/todosSlice'
import ContentFooter from './ContentFooter'


const Content = () => {
    const dispatch = useDispatch();


    const items = useSelector((state) => state.todos.items)
    //console.log("items:",items);


    return (

        <Stack spacing={5}
            bg="white" mt="10px"
        >
            {/**
             SLICE DAKI ELEMANLARI MAPLEME
             */}
            {items.map((item) => (



                <Flex

                    mt="5px"
                    mb="3px">
                    <Box  key={item.id} >
                        <Checkbox 
                        size='lg' 
                        colorScheme='orange' 
                        defaultChecked ml="5px"
                        onChange={()=>dispatch.toggle({id:item.id})}
                        >
                            <p style={item.complated ? { textDecoration: "line-through" } : { color: "black" }}>
                                {item.title}
                            </p>


                        </Checkbox>
                    </Box>
                    <Spacer />
                    <Box>
                        <Button colorScheme="telegram" variant="outline">X</Button>
                    </Box>
                </Flex>

            ))}


            <ContentFooter />

        </Stack>
    )
}

export default Content

