import { Box, Button, Checkbox, Flex, Spacer, Stack } from '@chakra-ui/react'
import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { toggle, destroy } from '../redux/todos/todosSlice'
import ContentFooter from './ContentFooter'


const Content = () => {
    const dispatch = useDispatch();


    const items = useSelector((state) => state.todos.items)
    //console.log("items:",items);

    //close button destroy and uyarı
    //()=>dispatch(destroy(item.id))

    const handleDestroy = (id) => {
        if (window.confirm("Are You Sure?")) {
            dispatch(destroy(id));
        }
    }


      //active filter controll
      const activeFilter =  useSelector((state) => state.todos.activeFilter)

    return (

        <Stack spacing={5}
            bg="white" mt="10px"
        >
            {/**
             SLICE DAKI ELEMANLARI MAPLEME
             */}
            {items.map((item) => (
                <Flex
                    key={item.id}
                    mt="5px"
                    mb="3px">
                    <Box   >
                        <Checkbox
                            size='lg'
                            colorScheme='orange'
                            ml="5px"
                            onChange={() => dispatch(toggle({ id: item.id }))}
                        >
                            <p style={item.complated ? { textDecoration: "line-through" } : { color: "black" }}>
                                {item.title}
                            </p>
                        </Checkbox>
                    </Box>
                    <Spacer />
                    <Box>
                        <Button
                            size='xs'
                            mr="8px"
                            colorScheme="red"
                            variant="outline"
                            onClick={() => handleDestroy(item.id)}
                        >
                            X
                        </Button>
                    </Box>
                </Flex>

            ))}


            <ContentFooter />

        </Stack>
    )
}

export default Content

