import { Box, Button, Checkbox, Flex, Spacer, Stack } from '@chakra-ui/react'
import React from 'react'
import { useSelector } from 'react-redux'
import ContentFooter from './ContentFooter'



const Content = () => {


    const items = useSelector((state) => state.todos.items)
    //console.log("items:",items);
    return (
        <Stack spacing={5}
            bg="white" mt="10px"
        >

            {items.map((item) => (


                <Flex 
                key={item.id}
                mt="5px" 
                mb="3px">
                    <Box >
                        <Checkbox style={item.comleted= as='del'} size='lg' colorScheme='orange' defaultChecked ml="5px">
                            {item.title}
                        </Checkbox>
                    </Box>
                    <Spacer />
                    <Box>
                        <Button colorScheme="telegram" variant="outline">X</Button>
                    </Box>
                </Flex>

            ))}


            <ContentFooter />

        </Stack>
    )
}

export default Content

