import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";


//get proc
export const getTodoAsync = createAsyncThunk('todos/getTodoAsync', async () => {
    const res = await axios(`${process.env.REACT_APP_API_BASE_ENDPOINT}/todos`);
    return res.data

})


//post proc
export const addTodoAsync = createAsyncThunk('todos/addTodoAsync', async (data) => {
    const res = await axios.post(`${process.env.REACT_APP_API_BASE_ENDPOINT}/todos`, data);
    return res.data
})

//toggle proc

export const toggleTodoAsync = createAsyncThunk('todos/toggleTodoAsync', async ({ id, data }) => {
    const res = await axios.patch(`${process.env.REACT_APP_API_BASE_ENDPOINT}/todos/${id}`, data);
    return res.data

})
//delete proc
export const removeTodoAsync =createAsyncThunk('todos/removeTodoAsync',async(id)=>{
    await axios.delete(`${process.env.REACT_APP_API_BASE_ENDPOINT}/todos/${id}`);
    return id


})


export const todosSlice = createSlice({
    name: 'todos',
    initialState: {
        items: [],
        isLoading: false,
        error: null,
        activeFilter: 'all',
        addNewTodoIsLoading: false,
        addNewTodoError: null,

    },
    reducers: {

/*
        toggle: (state, action) => {
            const { id } = action.payload;
            const item = state.items.find((item) => item.id === id);
            item.completed = !item.completed
        },*/
        /*
        destroy: (state, action) => {
            const id = action.payload;
            //silinmek istenen item dışı tüm itemları filtreleme
            const filtered = state.items.filter((item) => item.id !== id)
            //filtrelenen item ı state olarak verme
            state.items = filtered;
        },*/
        changeActiveFilter: (state, action) => {
            state.activeFilter = action.payload;
        },
        //button, cleaning
        clearCompleted: (state) => {
            const filtered = state.items.filter((item => item.completed === false));

            state.items = filtered;

        },

    },
    extraReducers: {
        //gettodos
        [getTodoAsync.pending]: (state, action) => {
            state.isLoading = true;
        },
        [getTodoAsync.fulfilled]: (state, action) => {
            state.items = action.payload;
            state.isLoading = false;
        },
        [getTodoAsync.rejected]: (state, action) => {
            state.isLoading = false;
            state.error = action.error.message;
        },
        //addtodo 
        [addTodoAsync.pending]: (state, action) => {
            state.addNewTodoIsLoading = true;
        },
        [addTodoAsync.fulfilled]: (state, action) => {
            state.items.push(action.payload);
            state.addNewTodoIsLoading = false;
        },
        [addTodoAsync.rejected]: (state, action) => {
            state.addNewTodoIsLoading = false;
            state.addNewTodoError = action.error.message;
        },

        //toggle todo
        [toggleTodoAsync.fulfilled]:(state,action)=>{
            const {id,completed}=action.payload;
            const index=state.items.findIndex(item =>item.id===id);
            state.items[index].completed=completed;
        },
        //remove todo
        [removeTodoAsync.fulfilled]:(state,action)=>{
            const id = action.payload;
            const filtered =state.items.filter((item)=>item.id !==id);
            state.items=filtered;
        },

    }
})


//Selectors
export const selectTodos = (state) => state.todos.items;

//all filtered
export const selectFilteredTodos = (state) => {
    if (state.todos.activeFilter === "all") {
        return state.todos.items;
    }

    return state.todos.items.filter((todo) =>
        state.todos.activeFilter === 'active'
            ? todo.completed === false
            : todo.completed === true
    )
}

export const selectActiveFilter = (state) => state.todos.activeFilter

export const { changeActiveFilter, clearCompleted } = todosSlice.actions;
export default todosSlice.reducer;
