import { Box, Button, Checkbox, Flex, Spacer, Stack } from '@chakra-ui/react'
import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { toggle, destroy,selectFilteredTodos, getTodoAsync } from '../redux/todos/todosSlice'
import ContentFooter from './ContentFooter'

//let filtered=[];

const Content = () => {
    const dispatch = useDispatch();

    //filtrede
    const filtredTodos=useSelector(selectFilteredTodos)


    //const items = useSelector(selectTodos)
    //console.log("items:",items);

    //close button destroy and uyarı
    //()=>dispatch(destroy(item.id))

    const handleDestroy = (id) => {
        if (window.confirm("Are You Sure?")) {
            dispatch(destroy(id));
        }
    }


    //active filter controll
    //slice a filter işlemi taşıdığımız için activefilter a ihtiyaç kalmadı
    //const activeFilter = useSelector((state) => state.todos.activeFilter)

/*
    filtered=items;
    if (activeFilter !== 'all') {
        //active olanlar complate ı false olanları getir
        filtered = items.filter((todo) =>
            activeFilter === 'active' ? todo.completed === false : todo.complated === true
        )
    }*/


    //get api

    useEffect(() => {
       dispatch(getTodoAsync())
    }, [dispatch])

    return (

        <Stack spacing={5}
            bg="white" mt="10px"
        >
            {/**
             SLICE DAKI ELEMANLARI MAPLEME
             */}
            {filtredTodos.map((item) => (
                <Flex
                    key={item.id}
                    mt="5px"
                    mb="3px">
                    <Box   >
                        <Checkbox
                            size='lg'
                            colorScheme='orange'
                            ml="5px"
                            onChange={() => dispatch(toggle({ id: item.id }))}
                        >
                            <p style={item.completed ? { textDecoration: "line-through" } : { color: "black" }}>
                                {item.title}
                            </p>
                        </Checkbox>
                    </Box>
                    <Spacer />
                    <Box>
                        <Button
                            size='xs'
                            mr="8px"
                            colorScheme="red"
                            variant="outline"
                            onClick={() => handleDestroy(item.id)}
                        >
                            X
                        </Button>
                    </Box>
                </Flex>

            ))}


            <ContentFooter />

        </Stack>
    )
}

export default Content

