import {  FormControl, Input, useToast } from '@chakra-ui/react'
import { useState } from 'react'
import { useDispatch,useSelector } from 'react-redux'

//import { store } from '../redux/store'
import { addTodoAsync } from '../redux/todos/todosSlice'
import Error from './Error'
import Loading from './Loading'


function FormTodo() {
    const toast = useToast()

    const [title, setTitle] = useState('')
    const dispatch = useDispatch()

    //loading 
    const isLoading = useSelector(state=>state.todos.addNewTodoIsLoading)

    const handleSubmit = async(e) => {
        if (e && e.preventDefault) { e.preventDefault(); }


       if(!title)(
        //window.confirm("Not Empty!")
        toast({
            title: 'Empty!.',
            description: "Please fill in the required field.",
            status: 'error',
            duration: 5000,
            isClosable: true,
          })

       )
       else{
       await dispatch(addTodoAsync({ title }))
       }
        setTitle('')
        //console.log({ id: newId, title, complated: false });

    }
    //console.log(title)

    //error
    const error =useSelector(state=>state.todos.addNewTodoError)


    return (
        <form
            key={setTitle}
            onSubmit={handleSubmit}
            style={{ marginTop: "10px" }}
        >
            <FormControl
                bg="white"
                display="flex"
                alignItems="center"
            >
                <Input
                disabled={isLoading}
                    placeholder="Let's To do"
                    value={title}
                    onChange={(e)=>setTitle(e.target.value)}
                />
               {
                isLoading &&  <span
                style={{paddingRight:10}}
                >Loading...</span>

               }
            </FormControl>
            {error&&<Error message={error}/>}
        </form>
    )
}

export default FormTodo
