import { createSlice } from "@reduxjs/toolkit";


 export const todosSlice = createSlice({
    name:'todos',
    initialState:{
        items:[

            {
                id:'1',
                title:'Learn React',
                complated: true,
            },
            {
                id:'2',
                title:'Learn JavaScript',
                complated: false,
            },

        ],
        reducers:{
            addTodo:(state,action)=>{
                state.items.push(
                    {id:'3',title:'test',complated:true}
                    )
            }
        },
    }
 })


 export default todosSlice.reducer;
