//https://api.openweathermap.org/data/2.5/weather?lat={lat}&lon={lon}&appid={API key}

import axios from "axios";
import { useState } from "react";

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';



const App = () => {


    const [weather, setweather] = useState()

    //kullanıcının koordnat bilgilerini alacak
    const getWeatherData = async (lat, lon) => {

        //get api
        const key = process.env.REACT_APP_WEATHER_API_KEY;

        try {

        const {data} = await axios
        .get(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${key}`)
        setweather(data)

        } catch  {
            toast('🦄 Wow so easy!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                });
        }


    }

    return <div>
        Hava
        <ToastContainer />
    </div>
};

export default App;

//https://api.openweathermap.org/data/2.5/weather?q=istanbul&appid=ddff52d39fc33f81516007fc69230762
