//https://api.openweathermap.org/data/2.5/weather?lat={lat}&lon={lon}&appid={API key}

import axios from "axios";
import { useEffect, useState } from "react";


import { usePosition} from 'use-position';


const App = () => {

    const [latitude, longitude ] = usePosition()

    const [weather, setweather] = useState()

    //kullanıcının koordnat bilgilerini alacak
    const getWeatherData = async (lat, lon) => {
        const key = process.env.REACT_APP_WEATHER_API_KEY;
        const lang = navigator.language.split("-")[0];

        try {
          const { data } = await axios.get(
            `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${key}&lang=${lang}&units=metric`
          );
          setWeather(data);
        } catch {
          alert("Veri alinirken hata olustu.");
        }
      };


    useEffect(() => {
        latitude && longitude && getWeatherData(latitude, longitude);
      }, [latitude, longitude]);

        console.log('latitude: ',latitude, 'longitude :', longitude, "weather:" ,weather);


    return <div>
        Hava

    </div>
};

export default App;

